package gui.form.base;import java.awt.Graphics;import java.util.ArrayList;import gui.inputHandlers.clicks.MouseClick;/** * A class that represents a menu bar on which clickable menuHeaders reside. */public class MenuBar extends FormObject {	ArrayList<MenuHeader> menuHeaders;	public MenuBar(int x, int y, int width, int height) {		super(x, y, width, height);		this.setMenuHeaders(new ArrayList<MenuHeader>());	}	public void addMenuHeader(MenuHeader header) {		this.menuHeaders.add(header);		updateEnabled();	}	public void deleteMenuHeader(MenuHeader header) {		this.menuHeaders.remove(header);	}	public ArrayList<MenuHeader> getMenuHeaders() {		return menuHeaders;	}	private void setMenuHeaders(ArrayList<MenuHeader> menuHeaders) {		this.menuHeaders = menuHeaders;	}	@Override	public void onClick(MouseClick click) {		for (MenuHeader header : this.getMenuHeaders()) {			if (header.isIn(click.getX(), click.getY())) {				header.onClick(click);			}		}	}	@Override	public void draw(Graphics g) {		g.fillRect(this.getX(), this.getY(), this.getWidth(), this.getHeight());		for (MenuHeader header : this.getMenuHeaders()) {			header.draw(g);		}	}	@Override	protected void onAction() {	}		public void updateEnabled() {		for (MenuHeader header : this.getMenuHeaders()) {			header.setEnabled(header.canBeEnabled());		}	}}